import altair as altimport pandas as pddf = pd.read_csv("/Users/jbenes/Downloads/Electric_Vehicle_Population_Data-2.csv")df_filtered = df[(df['Electric Range'] > 0) & (df['Base MSRP'] > 0)].copy()sample_size = min(5000, len(df_filtered))df_sampled = df_filtered.sample(sample_size)type_dropdown = alt.binding_select(    options=sorted(df_sampled['Electric Vehicle Type'].unique()),    name='Vehicle Type: ')type_select = alt.param(    name='vehicle_type_filter',    bind=type_dropdown,    value=df_sampled['Electric Vehicle Type'].iloc[0])brush = alt.selection_interval(name='range_select')scatter = alt.Chart(df_sampled).mark_point(filled=True, size=60).encode(    x=alt.X('Electric Range', title='Electric Range (miles)'),    y=alt.Y('Base MSRP', title='Base MSRP ($)'),    color=alt.condition(brush, 'Electric Vehicle Type', alt.value('lightgray')),    tooltip=['Make', 'Model', 'Model Year', 'Electric Range', 'Base MSRP']).add_params(    type_select,    brush).transform_filter(    alt.datum['Electric Vehicle Type'] == type_select).properties(    title='Electric Range vs Base MSRP by Vehicle Type',    width=700,    height=450)scatter.save('interactive_plot_new.html')